 locationRequest = LocationRequest.create();        locationRequest.setPriority(LocationRequest.PRIORITY_HIGH_ACCURACY);        locationRequest.setInterval(UPDATE_INTERVAL_IN_MILLISECONDS);        locationRequest.setFastestInterval(FASTEST_UPDATE_INTERVAL_IN_MILLISECONDS);//        locationRequest.setInterval(3000);        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.M) {            if (ActivityCompat.checkSelfPermission(context,                    Manifest.permission.ACCESS_FINE_LOCATION) != PackageManager.PERMISSION_GRANTED &&                    ActivityCompat.checkSelfPermission(context, Manifest.permission.ACCESS_COARSE_LOCATION) != PackageManager.PERMISSION_GRANTED) {                ActivityCompat.requestPermissions(Objects.requireNonNull(this),                        new String[]{Manifest.permission.ACCESS_FINE_LOCATION,                                Manifest.permission.ACCESS_COARSE_LOCATION},                        MY_PERMISSIONS_REQUEST_LOCATION);                return;            }        }        mFusedLocationClient.requestLocationUpdates(locationRequest, mLocationCallback, Looper.myLooper());    }    private final LocationCallback mLocationCallback = new LocationCallback() {        @SuppressLint("SetTextI18n")        @Override        public void onLocationResult(LocationResult locationResult) {            super.onLocationResult(locationResult);            if (locationResult.getLastLocation() == null)                return;            final Location currentLocation = locationResult.getLastLocation();            LatLng startLatLng = new LatLng(currentLocation.getLatitude(), currentLocation.getLongitude());            lat = startLatLng.latitude;            lng = startLatLng.longitude;            Log.e("currentlocation", "currentlocation" + lat + "," + lng);            new Handler().postDelayed(() -> {                if (Utilities.getInstance(context).isNetworkAvailable()) {                    Geocoder geocoder_new = new Geocoder(context, Locale.getDefault());                    Log.e("geocoder_new", "geocoder_new" + geocoder_new.toString());                    try {                        List<Address> listAddresses1 = geocoder_new.getFromLocation(lat, lng, 1);                        if (listAddresses1 != null && listAddresses1.size() > 0) {                            if (listAddresses1.get(0).getAddressLine(0) != null && !listAddresses1.get(0).getAddressLine(0).equalsIgnoreCase("")) {                                Log.e("locationame", "locationame" + listAddresses1.get(0).getAddressLine(0));                                locationname_txt.setText(listAddresses1.get(0).getSubLocality() + " , " + listAddresses1.get(0).getLocality());                                no_lat_long_tv.setText(listAddresses1.get(0).getSubLocality() + " , " + listAddresses1.get(0).getLocality());                            } else {                                locationname_txt.setText("No Location found");                                blinking_txt.setText("Google Unable to get your current location,please come to location to punch attendance");                                show_txt.setVisibility(View.GONE);                            }                        }                    } catch (Exception e) {                        e.printStackTrace();                    }                }            }, 50);            if (mFusedLocationClient != null)                mFusedLocationClient.removeLocationUpdates(mLocationCallback);        }    };    @Override    public void onStart() {        super.onStart();        new Handler().postDelayed(() -> {            try {                LocationManager service = (LocationManager) getSystemService(LOCATION_SERVICE);                enabled = Objects.requireNonNull(service).isProviderEnabled(LocationManager.GPS_PROVIDER);                if (!enabled) {                    showAlertGps("Please enable GPS first");                } else {                    if (isGooglePlayServicesAvailable()) {                        mFusedLocationClient = LocationServices.getFusedLocationProviderClient(context);                        startCurrentLocationUpdates();                    }                }            } catch (Exception e) {                e.printStackTrace();            }        }, 3000);    }    @Override    public void onStop() {        super.onStop();        if (mFusedLocationClient != null)            mFusedLocationClient.removeLocationUpdates(mLocationCallback);    }    @Override    public void onDestroy() {        super.onDestroy();        mFusedLocationClient = null;    }    @Override    public void onResume() {        super.onResume();        new Handler().postDelayed(() -> {            try {                LocationManager service = (LocationManager) getSystemService(LOCATION_SERVICE);                enabled = Objects.requireNonNull(service).isProviderEnabled(LocationManager.GPS_PROVIDER);                if (!enabled) {                    showAlertGps("Please enable GPS first");                } else {                    if (isGooglePlayServicesAvailable()) {                        mFusedLocationClient = LocationServices.getFusedLocationProviderClient(context);                        startCurrentLocationUpdates();                    }                }            } catch (Exception e) {                e.printStackTrace();            }        }, 3000);    }    private void showAlertGps(String message) {        AlertDialog.Builder builder = new AlertDialog.Builder(context);        builder.setMessage(message);        builder.setPositiveButton("Ok", (dialog, which) -> {            try {                dialog.dismiss();                Intent intent = new Intent(Settings.ACTION_LOCATION_SOURCE_SETTINGS);                startActivity(intent);            } catch (Exception e) {                e.printStackTrace();            }        });        builder.create();        builder.show();    }    private void check_internet() {        ConnectivityManager connectivitymanager = (ConnectivityManager) getSystemService(Context.CONNECTIVITY_SERVICE);        NetworkInfo[] networkInfo = connectivitymanager.getAllNetworkInfo();        for (NetworkInfo netInfo : networkInfo) {            if (netInfo.getTypeName().equalsIgnoreCase("WIFI")) {                if (netInfo.isConnected()) {                    String wifiIp = myUtlis.getWifiIPAddress();                    appSession.setIP_address(wifiIp);                    Log.e("wifiIp", "check_internet: " + wifiIp);                }            } else {                if (netInfo.isConnected()) {                    String mobileIp = myUtlis.getMobileIPAddress();                    appSession.setIP_address(mobileIp);                    Log.e("mobileIp", "check_internet: " + mobileIp);                }            }        }    }    private void getAllLatitude() {        String url = Cons.serverUrl + "api/android/App_GetEmployeeLatLongRecord";        Log.e("SAVED Url >>>>>> ", url);//        final ProgressDialog pDialog = ProgressDialog.show(Activity_Punch_Attendence.this, null, null);//        pDialog.setContentView(R.layout.progress_loader);//        Objects.requireNonNull(pDialog.getWindow()).setBackgroundDrawable(new ColorDrawable(Color.TRANSPARENT));//        pDialog.setCancelable(false);//        pDialog.show();        JSONObject mainJson = new JSONObject();        try {            mainJson.put("EmployeeId", et_employeeIdStr);            Log.e("EmployeeId", "EmployeeUserId" + mainJson.toString().trim());        } catch (Exception e) {            e.printStackTrace();        }        final String requestBody = mainJson.toString();        StringRequest getRequest = new StringRequest(Request.Method.POST, url,                response -> {                    Log.e("response", ">>result>>>>>" + response.toString());                    try {                        if (!response.equalsIgnoreCase("")) {                            if (!response.contains("<!DOCTYPE html>")) {                                try {                                    JSONObject jsonObject = new JSONObject(response);                                    Log.e("bind_response", "onResponse: " + jsonObject);                                    if (jsonObject.getString("ResultStatus").equals("True")) {                                        if (jsonObject.getString("Record").equals("")) {                                            //employee_details_ll.setVisibility(View.GONE);                                            show_ll.setVisibility(View.GONE);                                            cardview_user_office.setVisibility(View.GONE);                                            no_lat_long_cv.setVisibility(View.VISIBLE);                                        } else {                                            show_ll.setVisibility(View.VISIBLE);                                            cardview_user_office.setVisibility(View.VISIBLE);                                            no_lat_long_cv.setVisibility(View.GONE);                                            JSONArray jsonArray = jsonObject.getJSONArray("Record");                                            Log.e("jsonArray_length", "" + jsonArray.length());                                            if (jsonArray.length() > 0) {                                                cardview_user_office.setVisibility(View.VISIBLE);                                                show_ll.setVisibility(View.VISIBLE);                                                no_lat_long_cv.setVisibility(View.GONE);                                                for (int i = 0; i < jsonArray.length(); i++) {                                                    pojoLatLongs.add(new PojoLatLong(jsonArray.getJSONObject(i).getString("NameofOffice"),                            