package apps.janshakti.allactivities;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.ImageView;import android.widget.LinearLayout;import androidx.annotation.Nullable;import com.google.android.material.textfield.TextInputEditText;import com.squareup.picasso.Picasso;import apps.janshakti.R;import apps.janshakti.activity.BaseActivity;public class ProfileActivity extends BaseActivity implements View.OnClickListener {    private LinearLayout lyr_change_password, lyr_rate, lyr_share,logout_ll,feedback_ll;    private TextInputEditText et_name, et_mail, et_mobile;    private ImageView iv_bck,image_iv;    private Button btn_submit;    @Override    public void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_profile);        iv_bck = findViewById(R.id.iv_bck);        image_iv = findViewById(R.id.image_iv);        et_name = findViewById(R.id.et_name);        et_mail = findViewById(R.id.et_mail);        et_mobile = findViewById(R.id.et_mobile);        et_name.setText(appSession.getUsername());        et_mail.setText(appSession.getEmailAddress());        et_mobile.setText(appSession.getMobile());        lyr_change_password = findViewById(R.id.lyr_change_password);        lyr_rate = findViewById(R.id.lyr_rate);        lyr_share = findViewById(R.id.lyr_share);        btn_submit = findViewById(R.id.btn_submit);        logout_ll = findViewById(R.id.logout_ll);        feedback_ll = findViewById(R.id.feedback_ll);        iv_bck.setOnClickListener(this);        btn_submit.setOnClickListener(this);        lyr_change_password.setOnClickListener(this);        lyr_rate.setOnClickListener(this);        lyr_share.setOnClickListener(this);        logout_ll.setOnClickListener(this);        feedback_ll.setOnClickListener(this);        if(!appSession.getImage().isEmpty()){            Picasso.get().load(appSession.getImage()).placeholder(R.drawable.logo).into(image_iv);        }    }    @Override    public void onClick(View view) {        if (view.getId() == R.id.iv_bck) {            finish();        } else if (view.getId() == R.id.lyr_change_password) {            gotoActivity(ChangePasswordActivity.class);        }else if (view.getId() == R.id.feedback_ll) {            gotoActivity(FeedbackActivity.class);        } else if (view.getId() == R.id.lyr_rate) {            rate_app();        } else if (view.getId() == R.id.lyr_share) {            share();        }else if (view.getId() == R.id.logout_ll) {            showLogoutAlert();        }    }    @Override    public void onBackPressed() {        finish();    }}